mapper.delete:
delete from ps_student_question


        and id = #{id}


        and exam_id = #{examId}


        and person_id = #{personId}


        and answer_sheet_item_id = #{answerSheetItemId}


        and ques_id = #{quesId}


        and is_optional = #{isOptional}


        and is_recommend = #{isRecommend}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_del = #{isDel}

mapper.update:
update ps_student_question


        id = #{id},


        exam_id = #{examId},


        person_id = #{personId},


        answer_sheet_item_id = #{answerSheetItemId},


        ques_id = #{quesId},


        is_optional = #{isOptional},


        is_recommend = #{isRecommend},


        create_time = #{createTime},


        update_time = #{updateTime},


        is_del = #{isDel},




        and id = #{id}


        and exam_id = #{examId}


        and person_id = #{personId}


        and answer_sheet_item_id = #{answerSheetItemId}


        and ques_id = #{quesId}


        and is_optional = #{isOptional}


        and is_recommend = #{isRecommend}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_del = #{isDel}

mapper.select:
select * from ps_student_question


        and id = #{id}


        and exam_id = #{examId}


        and person_id = #{personId}


        and answer_sheet_item_id = #{answerSheetItemId}


        and ques_id = #{quesId}


        and is_optional = #{isOptional}


        and is_recommend = #{isRecommend}


        and create_time = #{createTime}


        and update_time = #{updateTime}

        and is_del = 0

SELECT ps.* FROM ps_examinee pe INNER JOIN ps_student_question ps ON ps.exam_id = pe.exam_id AND ps.person_id = pe.person_id AND ps.is_del = 0 WHERE pe.is_del = 0
AND pe.exam_id = #{examId}
AND pe.clazz_id = #{clazzId}
